CREATE TABLE IF NOT EXISTS hr_user( userno int AUTO_INCREMENT, username varchar(63) NOT NULL, firstname varchar(63) NOT NULL, lastname varchar(63) DEFAULT NULL, affiliation VARCHAR(255) DEFAULT NULL, jobtitle VARCHAR(63) DEFAULT NULL, photo_url varchar(255) DEFAULT NULL, email varchar(255) DEFAULT NULL, countrycode varchar(7) DEFAULT null, primarycontact varchar(15) DEFAULT NULL, passphrase varchar(255) NOT NULL, authkey VARCHAR(255) DEFAULT NULL, createtime TIMESTAMP DEFAULT CURRENT_TIMESTAMP, lastupdatetime TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, isactive int DEFAULT 0, userstatusno int DEFAULT 1, PRIMARY KEY(userno), CONSTRAINT uk_user_username UNIQUE(username) ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
